  # Impute missing data
  mice(printFlag=FALSE) |> complete()


#----- Constitution-level data -----#
library(plm)
consts = chron2 |> 
  merge(vp) |>
  mutate(over19 = (age>19),
         over31 = (age>31), .after=age) |>
  mutate(parliamentary = (system=="Parliamentary"),

