```{.r .cell-code}
#----- Setup -----#
library(lme4)

# Variable information
key = read.csv("data/formgov.csv")
outcomes = key |> 
  filter(type=="outcome") |>
  select(outcome=variable, outcome_name=concept)
predictors = key |>
  filter(type=="predictor") |>
  mutate(i = row_number()) |>
  select(variable, i)


# Model settings
alpha=.05


#----- Prepare data ----#
vp = veto |>

  # Select variables
  select(abb, country_name, year, pop, 
         libdem, civil, corrupt, fragility, econ, top1,
         nparties, checks, conflict, gdppc, lifeexp, pop, 
         ethnic, system) |>

  # Fix data
  mutate(checks = ifelse(checks<0, NA, checks),
         system = as.factor(system),
         ) |>

  # Remove duplicate country-years
  group_by(abb, year) |>
  filter(row_number() == 1) |>
  ungroup() |>

  # Remove countries without much data
  #group_by(abb) |>
  #mutate(start = min(year)) |>
  #ungroup() |>
  #filter(start<2001, year>=2000, year<2020) |>

  # Make dummy variables
  dummy_cols(c("system"), remove_most_frequent_dummy=TRUE) |>
  rename(system2=`system_President-parliamentary`,
         system3=`system_Premier-presidential`, system4=`system_Parliamentary`) |>

  # Impute missing data
  mice(printFlag=FALSE) |> complete()


#----- Function to adjust coefficients based on interactions -----#
adjust_coefs_for_system <- function(coefs, system_name){
  coefs <- coefs |>
    mutate(
      est = case_when(
        variable == "nparties" ~ est + coefs[coefs$variable == paste0(system_name, ":nparties"),]$est,
        variable == "ethnic" ~ est + coefs[coefs$variable == paste0(system_name, ":ethnic"),]$est,
        variable == "(Intercept)" ~ est + coefs[coefs$variable == system_name,]$est,
        TRUE ~ est
      ),
      se = case_when(
        variable == "nparties" ~ sqrt(se^2 + coefs[coefs$variable == paste0(system_name, ":nparties"),]$se^2),
        variable == "ethnic" ~ sqrt(se^2 + coefs[coefs$variable == paste0(system_name, ":ethnic"),]$se^2),
        TRUE ~ se
      ),
      ci_lower = est - se*1.96,
      ci_upper = est + se*1.96
    )
  return(coefs)
}



#----- Function for fitting models -----#
vetomod = function(data, y_var) {

  # Y variable and lags
  data$y_t = data[[y_var]]
  data$y_l1 = lag(data$y_t, 1)
  data$y_l2 = lag(data$y_t, 2)
  data$y_l3 = lag(data$y_t, 3)
  data$y_l4 = lag(data$y_t, 4)

  # Model with original scale
  mod1 = lmer(y_t ~ y_l1 + y_l2 + y_l3 + y_l4 + system*nparties + system*ethnic + 
                conflict + gdppc + lifeexp + (1 + system|abb), 
              data=data)

  # Get coefficients in original scale
  mod1_coefs = summary(mod1)$coefficients |>
    as.data.frame() |>
    rownames_to_column("variable") |>
    select(variable, est_orig=Estimate, se_orig=`Std. Error`)

  # Model with standardized scales
  data2 = data |> mutate(across(c(libdem:ethnic, y_t:y_l4), ~as.numeric(scale(.x))))
  mod2 = lmer(y_t ~ y_l1 + y_l2 + y_l3 + y_l4 + system*nparties + system*ethnic + 
                conflict + gdppc + lifeexp + (1 + system|abb), 
              data=data2)

  # Get standardized coefficients
  coefs = summary(mod2)$coefficients |>
    as.data.frame() |>
    rownames_to_column("variable") |>
    select(variable, est=Estimate, se=`Std. Error`, t=`t value`) |>

    # Merge original coefficients and variable names
    merge(mod1_coefs) |>
    merge(key) |>
    merge(predictors) |>
    arrange(i) |>

    # Compute confidence intervals
    mutate(ci_lower = est - se*1.96,
           ci_upper = est + se*1.96,
           concept2 = row_number()) |>
  filter(!grepl("y_", variable))

  # Adjust coefficients based on system interactions
  coefs_system2 = adjust_coefs_for_system(coefs, "systemParliamentary")
  coefs_system3 = adjust_coefs_for_system(coefs, "systemPremier-presidential")
  coefs_system4 = adjust_coefs_for_system(coefs, "systemPresident-parliamentary")

  # Bind rows to store all coefficients together
  coefs_all = bind_rows(
    original = coefs,
    adj_parl = coefs_system2,
    adj_prem = coefs_system3,
    adj_prpa = coefs_system4,
    .id = "model_version"
  )

  # Return coefficient table and models
  return(list(coefs=coefs_all, mod_orig=mod1, mod_std=mod2))

}


#----- Fit models -----#
mod_dem = vetomod(vp, "libdem")
mod_civil = vetomod(vp, "civil")
mod_corrupt = vetomod(vp, "corrupt")
mod_frag = vetomod(vp, "fragility")
mod_econ = vetomod(vp, "econ")
mod_ineq = vetomod(vp, "top1")

# Put model data together
mods = bind_rows(libdem=mod_dem$coefs, civil=mod_civil$coefs, 
                 corrupt=mod_corrupt$coefs, frag=mod_frag$coefs,
                 econ=mod_econ$coefs, ineq=mod_ineq$coefs,
                 .id="outcome") |>
  merge(outcomes) |>
  mutate(sig = case_when(
           (ci_lower < 0) & (ci_upper < 0) ~ "Decreases",
           (ci_lower > 0) & (ci_upper > 0) ~ "Increases",
           TRUE ~ "No effect on"),
         result = paste(sig, tolower(outcome_name))) |>
  arrange(outcome, i)


#----- Interactive figure -----#
coef_plot = function(data, y_var, version) {

  # Update data
  data = filter(data, outcome==y_var, model_version==version)
  y_name = key$concept[key$variable==y_var]
  y_measure = tolower(key$measure[key$variable==y_var])
  if (version != "original") data = filter(data, !grepl("system", variable))

  # Create plot
  highchart() |>

    # Error bars
    hc_add_series(
      data, hcaes(x=concept2, low=ci_lower, high=ci_upper, group=result), 
      type="columnrange", opacity=.5, grouping=FALSE, linkedTo="estimates",
      enableMouseTracking=FALSE, showInLegend=FALSE,
    ) |>

    # Bubbles
    hc_add_series(
      data, hcaes(x=concept2, y=est, group=result), 
      type="scatter", id="estimates",
      tooltip=list(headerFormat=NULL, pointFormat=paste(
        bullet, "<b>{point.measure}</b><br>", "{point.result}<br>",
        "Standardized coefficient: <b>{point.est:.2f}</b><br>",
        "Coefficient on original scale: <b>{point.est_orig:.2f}</b><br>"
      ))
    ) |>

    # Titles
    hc_title(text=paste("Determinants of", y_name)) |>
    hc_subtitle(text=paste("Mixed effects models of", y_measure)) |>
    hc_xAxis(title=list(enabled=FALSE), crosshair=FALSE,
             labels=list(style=list(fontSize="1.1em")),
             categories=c(NA, unique(filter(mods, outcome=="libdem")$concept))) |>
    hc_yAxis(title=list(text=paste("Estimated effect on", y_name)),
             plotLines=list(list(width=2, value=0))) |>

    # Formatting
    hc_chart(inverted=TRUE) |>
    hc_morse(scatter=TRUE) |>
    hc_colors(c("#FF6969", "#1450A3", "#B9B4C7")) |>
    hc_legend(enabled=FALSE) |>
    hc_exporting(enabled=TRUE, filename="forms-gov", sourceWidth=750)

}
```
