```{.r .cell-code}
#----- Setup -----#

# Election system descriptions
el_systems = c(
  paste("<b>Plurality elections</b><br>",
        "each district has one seat, which goes to<br>",
        "the candidate with the most votes"),
  paste("<b>Mixed electoral methods</b><br>",
        "plurality elections for some seats and<br>",
        "proportional representation for others"),
  paste("<b>Proportional representation</b><br>",
        "districts have multiple seats, which are<br>",
        "split among the parties based on how many<br>",
        "votes they received")
)

# Election system abbreviations
el_abb = c("Plurality", "Mixed", "PR")

# Measure names
j = c(seats="Seat shares of the largest party",
      votes="Vote shares of the largest party",
      all_parties="Number of parties in national legislatures")

# Units
u = c("%", "%", "")


#----- Prepare data -----#
wta2 = wta |>

  # Reshape data
  filter(!is.na(seats)) |>
  pivot_longer(all_parties:votes, names_to="variable", values_drop_na=TRUE) |>
  mutate(measure = as.character(factor(variable, levels=names(j), labels=j)),
         u = as.character(factor(variable, levels=names(j), labels=u))) |>

  # Collapse by electoral system
  group_by(elections, variable, measure, u) |>
  summarize(n = n(), np=NA,
            avg = mean(value),
            median = median(value),
            se = sd(value)/sqrt(n)) |>
  ungroup() |>

  # Bring in data of all parties
  bind_rows(vparties2) |>

  # Add labels to electoral system variables
  mutate(elections2 = factor(elections, labels=el_systems),
         elections = factor(elections, labels=el_abb))


#----- Interactive graph -----#
create_barchart = function(y_var, unit, all=FALSE) {

  # Subset data
  wta2 |> filter(variable==y_var) |>

  # Bars
  hchart(hcaes(x=elections, y=avg, group=elections), type="column",
       stacking="overlap", groupPadding=.05,
       dataLabels=list(enabled=TRUE, useHTML=TRUE, zIndex=3,
                       format="<b>{point.y:.1f}{point.u}</b>"
       )) |>

  # Tooltip
  hc_tooltip(
    headerFormat=NULL, 
    pointFormat=paste(
      "{point.elections2}<br><br> {point.measure}<br>", 
      bullet, "Average: <b>{point.y:.1f}{point.u}</b><br>",
      bullet, "Median: <b>{point.median:.1f}{point.u}</b><br>",
      bullet, "Standard error: <b>{point.se:.1f}</b><br>",
      bullet, "Countries: <b>{point.n}</b><br>",
      ifelse(all, "Parties: <b>{point.np}</b>", "")
    )) |>

  # Titles
  hc_xAxis(title=list(enabled=FALSE)) |>
  hc_yAxis(title=list(enabled=FALSE), opposite=TRUE,
           labels=list(format=paste0("{text}", unit))) |>

  # Formatting
  hc_morse() |>
  hc_colors(c("#285b85", "#47888D", "#67b595")) |>
  hc_legend(enabled=FALSE)

}
